using System;
using System.Collections.Generic;
using System.Linq;
using System.Web;
using System.Web.Mvc;
using WebFotos.Models;

namespace WebFotos.Controllers
{
    public class FotoController : Controller
    {
        // GET: Foto
        public ActionResult Listar()
        {
            List<Foto> imageColl = new List<Foto>()
            {
                new Foto
                {
                    Id = 1,
                    DataPublicacao = DateTime.Now.AddDays(-1),
                    Descricao = "Chrome Desc",
                    Titulo = "Chrome Titulo",
                    Imagem = "data:image/png;base64,"
                }
            };

            return View(imageColl);
        }

        public ActionResult Upload()
        {
            return View();
        }
    }
}